query:
query detailedCartItems($orderCartId: ID!, $corporateIndividualOrdersEnabled: Boolean, $deliveryOptionType: DeliveryOptionType, $isCardPayment: Boolean) {
  orderCart(id: $orderCartId, corporateIndividualOrdersEnabled: $corporateIndividualOrdersEnabled, deliveryOptionType: $deliveryOptionType, isCardPayment: $isCardPayment) {
    id
    ...InvalidItemsFragment
    subtotal
    orders {
      id
      consumer {
        id
        firstName
        lastName
        localizedNames {
          informalName
          formalName
          formalNameAbbreviated
          __typename
        }
        __typename
      }
      isSubCartFinalized
      paymentCard {
        id
        stripeId
        __typename
      }
      paymentLineItems {
        subtotal
        taxAmount
        subtotalTaxAmount
        feesTaxAmount
        serviceFee
        __typename
      }
      orderItems {
        id
        options {
          id
          name
          quantity
          price
          nestedOptions
          __typename
        }
        nestedOptions
        specialInstructions
        substitutionPreference
        quantity
        singlePrice
        priceOfTotalQuantity
        priceDisplayString
        continuousQuantity
        unit
        purchaseType
        estimatedPricingDescription
        increment {
          decimalPlaces
          unitAmount
          __typename
        }
        discounts {
          ...OrderItemDiscountFragment
          __typename
        }
        item {
          id
          imageUrl
          name
          price
          minAgeRequirement
          category {
            title
            __typename
          }
          extras {
            id
            title
            description
            __typename
          }
          __typename
        }
        bundleStore {
          id
          name
          isPrimary
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment InvalidItemsFragment on OrderCart {
  invalidItems {
    itemId
    storeId
    itemQuantityInfo {
      discreteQuantity {
        quantity
        unit
        __typename
      }
      continuousQuantity {
        quantity
        unit
        __typename
      }
      __typename
    }
    name
    itemExtrasList
    menuId
    __typename
  }
  __typename
}

fragment OrderItemDiscountFragment on OrderItemDiscount {
  id
  finalMoney {
    ...DiscountMonetaryFieldsFragment
    __typename
  }
  badges {
    ...DiscountBadgeFragment
    __typename
  }
  adsMetadata {
    ...DiscountAdsMetadataFragment
    __typename
  }
  __typename
}

fragment DiscountMonetaryFieldsFragment on AmountMonetaryFields {
  currency
  displayString
  decimalPlaces
  unitAmount
  sign
  __typename
}

fragment DiscountBadgeFragment on Badge {
  type
  text
  backgroundColor
  styleType
  dlsTagSize
  placement
  leadingIcon
  leadingIconSize
  trailingIcon
  trailingIconSize
  endTime
  __typename
}

fragment DiscountAdsMetadataFragment on AdsMetadata {
  campaignId
  adGroupId
  auctionId
  __typename
}
